import React, { useEffect, useRef } from "react";
import PropTypes from "prop-types";
import "./TextArea.css";

const TextArea = ({ value, readOnly }) => {
  const containerRef = useRef(null);

  // Auto-scroll to bottom on new content
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop =
        containerRef.current.scrollHeight;
    }
  }, [value]);

  const renderContent = () => {
    const lines = value.split("\n");
    const elements = [];
    let inCodeBlock = false;
    let codeBlock = [];

    const formatInlineText = (text) => {
      const parts = text.split(/(`[^`]+`|\*\*[^*]+\*\*)/g);
      return parts.map((part, index) => {
        if (part.startsWith("`") && part.endsWith("`")) {
          return (
            <strong key={index} className="inline-code">
              {part.slice(1, -1)}
            </strong>
          );
        }
        if (part.startsWith("**") && part.endsWith("**")) {
          return (
            <strong key={index} className="inline-bold">
              {part.slice(2, -2)}
            </strong>
          );
        }
        return <span key={index}>{part}</span>;
      });
    };

    lines.forEach((line, index) => {
      if (line.startsWith("```")) {
        if (inCodeBlock) {
          const code = codeBlock.join("\n");
          elements.push(
            <div className="code-container" key={`code-${index}`}>
              <button
                className="copy-button"
                onClick={() => navigator.clipboard.writeText(code)}
              >
                Copy
              </button>
              <pre className="code-block">
                <code>{code}</code>
              </pre>
            </div>
          );
          codeBlock = [];
        }
        inCodeBlock = !inCodeBlock;
      } else if (inCodeBlock) {
        codeBlock.push(line);
      } else {
        elements.push(
          <p className="formatted-text" key={`text-${index}`}>
            {formatInlineText(line)}
          </p>
        );
      }
    });

    return elements;
  };

  return (
    <div className="form-group mt-3">
      <div
        ref={containerRef}
        className="formatted-text-container"
        aria-readonly={readOnly}
      >
        {renderContent()}
      </div>
    </div>
  );
};

TextArea.propTypes = {
  value: PropTypes.string.isRequired,
  readOnly: PropTypes.bool,
};

TextArea.defaultProps = {
  readOnly: true,
};

export default TextArea;
