import React, { useEffect, useRef, useState } from "react";
import TextInput from "./TextInput";
import TextArea from "./TextArea";
import "./PromptInput.css";

function PromptInput() {
  const wsRef = useRef(null);
  const streamTextRef = useRef(""); // üî• source of truth

  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);

  const [streamBuffer, setStreamBuffer] = useState("");
  const [streamingText, setStreamingText] = useState("");
  const [isStreaming, setIsStreaming] = useState(false);

  /* =====================================================
     1Ô∏è‚É£ CONNECT TO WEBSOCKET (ONCE ONLY)
  ===================================================== */

useEffect(() => {
  console.log("üü° [UI] Initializing WebSocket‚Ä¶");

  const ws = new WebSocket("ws://localhost:4000");
  wsRef.current = ws;

  ws.onopen = () => {
    console.log("üü¢ [UI] WebSocket connected");
  };

  ws.onmessage = (event) => {
    console.log("üì® [UI] Raw WS message:", event.data);

    let data;
    try {
      data = JSON.parse(event.data);
    } catch (e) {
      console.error("‚ùå [UI] Failed to parse WS message", e);
      return;
    }

    console.log("üì¶ [UI] Parsed WS message:", data);

    if (data.type === "token") {
      console.log("üü£ [UI] Token received:", data.content);

      setIsStreaming(true);
      setStreamBuffer((prev) => {
        console.log("üü† [UI] streamBuffer before:", prev);
        const next = prev + data.content;
        console.log("üü† [UI] streamBuffer after:", next);
        return next;
      });
    }

    if (data.type === "done") {
      console.log("‚úÖ [UI] Stream done");

      setIsStreaming(false);

      console.log(
        "üß† [UI] Final streamed text:",
        streamTextRef.current
      );

      setMessages((prev) => {
        const updated = [
          ...prev,
          { role: "assistant", content: streamTextRef.current },
        ];
        console.log("üìù [UI] Messages updated:", updated);
        return updated;
      });

      streamTextRef.current = "";
      setStreamingText("");
      setStreamBuffer("");
    }
  };

  ws.onerror = (err) => {
    console.error("‚ùå [UI] WebSocket error:", err);
  };

  ws.onclose = () => {
    console.log("üî¥ [UI] WebSocket closed");
  };

  return () => {
    console.log("‚ö™ [UI] Cleaning up WebSocket");
    ws.close();
  };
}, []);


  /* =====================================================
     2Ô∏è‚É£ WORD-BY-WORD STREAM RENDERER
  ===================================================== */

useEffect(() => {
  if (!streamBuffer) return;

  console.log("‚úèÔ∏è [UI] Rendering streamBuffer:", streamBuffer);

  const words = streamBuffer.split(/\s+/);
  if (words.length <= 1) return;

  const interval = setInterval(() => {
    const nextWord = words[0];

    console.log("‚û°Ô∏è [UI] Rendering word:", nextWord);

    streamTextRef.current += nextWord + " ";
    setStreamingText(streamTextRef.current);

    setStreamBuffer(words.slice(1).join(" "));
  }, 80);

  return () => clearInterval(interval);
}, [streamBuffer]);


  /* =====================================================
     SEND MESSAGE
  ===================================================== */

  const sendMessage = () => {
    if (!input.trim() || !wsRef.current) return;

    const updatedMessages = [
      ...messages,
      { role: "user", content: input },
    ];

    setMessages(updatedMessages);

    wsRef.current.send(
      JSON.stringify({
        model: "llama3",
        messages: updatedMessages,
      })
    );

    setInput("");
  };

  /* =====================================================
     RENDER
  ===================================================== */

  return (
    <div className="chat-page">
      <div className="chat-container">
        <h1>AI Chat (LLaMA)</h1>

        <TextArea
          value={[
            ...messages.map((m) =>
              m.role === "user"
                ? `You: ${m.content}`
                : `AI: ${m.content}`
            ),
            ...(isStreaming ? [`AI: ${streamingText}`] : []),
          ].join("\n\n")}
        />

        {isStreaming && (
          <div className="thinking">AI is typing‚Ä¶</div>
        )}

        <div className="input-wrapper">
          <TextInput
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onEnter={sendMessage}
            placeholder="Type a message and press Enter"
          />
        </div>
      </div>
    </div>
  );
}

export default PromptInput;
