import React, { useEffect, useRef, useState } from "react";
import TextInput from "./TextInput";
import TextArea from "./TextArea";
import "./PromptInput.css";

function PromptInput() {
  const wsRef = useRef(null);

  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);

  const [streamBuffer, setStreamBuffer] = useState("");
  const [streamingText, setStreamingText] = useState("");
  const [isStreaming, setIsStreaming] = useState(false);

  /* =====================================================
     1ï¸âƒ£ CONNECT TO WEBSOCKET (MISSING PIECE)
  ===================================================== */

  useEffect(() => {
    const ws = new WebSocket("ws://localhost:4000");
    wsRef.current = ws;

    ws.onopen = () => {
      console.log("âœ… UI connected to WebSocket");
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);

      // token streaming
      if (data.type === "token") {
        setIsStreaming(true);
        setStreamBuffer((prev) => prev + data.content);
      }

      // stream finished
      if (data.type === "done") {
        setIsStreaming(false);

        setMessages((prev) => [
          ...prev,
          { role: "assistant", content: streamingText },
        ]);

        setStreamingText("");
        setStreamBuffer("");
      }
    };

    ws.onerror = (err) => {
      console.error("âŒ WebSocket error:", err);
    };

    ws.onclose = () => {
      console.log("ðŸ”Œ WebSocket disconnected");
    };

    return () => ws.close();
  }, [streamingText]);

  /* =====================================================
     2ï¸âƒ£ WORD-BY-WORD RENDERER
  ===================================================== */

  useEffect(() => {
    if (!streamBuffer) return;

    const words = streamBuffer.split(/\s+/);
    if (words.length <= 1) return;

    const interval = setInterval(() => {
      setStreamingText((prev) => prev + words[0] + " ");
      setStreamBuffer(words.slice(1).join(" "));
    }, 80);

    return () => clearInterval(interval);
  }, [streamBuffer]);

  /* =====================================================
     SEND MESSAGE
  ===================================================== */

  const sendMessage = () => {
    if (!input.trim() || !wsRef.current) return;

    const updatedMessages = [
      ...messages,
      { role: "user", content: input },
    ];

    setMessages(updatedMessages);

    wsRef.current.send(
      JSON.stringify({
        model: "llama3",
        messages: updatedMessages,
      })
    );

    setInput("");
  };

  /* =====================================================
     RENDER
  ===================================================== */

  return (
    <div className="chat-page">
      <div className="chat-container">
        <h1>AI Chat (LLaMA)</h1>

        <TextArea
          value={[
            ...messages.map((m) =>
              m.role === "user"
                ? `You: ${m.content}`
                : `AI: ${m.content}`
            ),
            ...(isStreaming ? [`AI: ${streamingText}`] : []),
          ].join("\n\n")}
        />

        {isStreaming && (
          <div className="thinking">AI is typingâ€¦</div>
        )}

        <div className="input-wrapper">
          <TextInput
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onEnter={sendMessage}
            placeholder="Type a message and press Enter"
          />
        </div>
      </div>
    </div>
  );
}

export default PromptInput;
